'use client'

import { useState } from 'react'

import {
  ChevronDownIcon,
  ChevronRightIcon,
  ChevronLeftIcon,
  ChevronUpIcon,
  TruckIcon,
  EllipsisVerticalIcon
} from 'lucide-react'

import type { ColumnDef, PaginationState } from '@tanstack/react-table'
import {
  flexRender,
  getCoreRowModel,
  getFacetedMinMaxValues,
  getFacetedRowModel,
  getPaginationRowModel,
  getFacetedUniqueValues,
  getSortedRowModel,
  useReactTable
} from '@tanstack/react-table'

import { Avatar, AvatarFallback } from '@/components/ui/avatar'
import { Badge } from '@/components/ui/badge'
import { Button } from '@/components/ui/button'
import { Checkbox } from '@/components/ui/checkbox'
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuGroup,
  DropdownMenuItem,
  DropdownMenuTrigger
} from '@/components/ui/dropdown-menu'
import { Pagination, PaginationContent, PaginationEllipsis, PaginationItem } from '@/components/ui/pagination'
import { Progress } from '@/components/ui/progress'
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table'

import { usePagination } from '@/hooks/use-pagination'

import { cn } from '@/lib/utils'

const listItems = ['Share', 'Update', 'Refresh']

export type Item = {
  id: string
  vehicle: number
  startingRoute: string
  endingRoute: string
  warning: 'No warning' | 'Fuel problems' | 'Temperature not optimal' | 'Ecu not responding' | 'Oil leakage'
  progress: number
  progressColor?: string
}

const columns: ColumnDef<Item>[] = [
  {
    id: 'select',
    header: ({ table }) => (
      <Checkbox
        checked={table.getIsAllPageRowsSelected() || (table.getIsSomePageRowsSelected() && 'indeterminate')}
        onCheckedChange={value => table.toggleAllRowsSelected(!!value)}
        aria-label='Select all'
      />
    ),
    cell: ({ row }) => (
      <Checkbox
        checked={row.getIsSelected()}
        onCheckedChange={value => row.toggleSelected(!!value)}
        aria-label='Select row'
      />
    ),
    size: 50
  },
  {
    header: 'Vehicle',
    accessorKey: 'vehicle',
    cell: ({ row }) => (
      <div className='flex items-center gap-2'>
        <Avatar className='size-8'>
          <AvatarFallback className='bg-primary/10 text-primary'>
            <TruckIcon className='size-4' />
          </AvatarFallback>
        </Avatar>
        <span>VOL-{row.getValue('vehicle')}</span>
      </div>
    )
  },
  {
    header: 'Starting route',
    accessorKey: 'startingRoute',
    cell: ({ row }) => <span className='text-muted-foreground'>{row.getValue('startingRoute')}</span>
  },
  {
    header: 'Ending route',
    accessorKey: 'endingRoute',
    cell: ({ row }) => <span className='text-muted-foreground'>{row.getValue('endingRoute')}</span>
  },
  {
    header: 'Warnings',
    accessorKey: 'warning',
    cell: ({ row }) => <Badge className='bg-primary/10 text-primary px-1.5 capitalize'>{row.getValue('warning')}</Badge>
  },
  {
    header: 'Progress',
    accessorKey: 'progress',
    cell: ({ row }) => (
      <div className='flex items-center gap-3'>
        <Progress value={row.getValue('progress')} className={cn('w-43', row.original.progressColor)} />
        <span>{row.getValue('progress')}%</span>
      </div>
    )
  }
]

const VehicleRouteDatatable = ({ data }: { data: Item[] }) => {
  const pageSize = 5

  const [pagination, setPagination] = useState<PaginationState>({
    pageIndex: 0,
    pageSize: pageSize
  })

  const table = useReactTable({
    data,
    columns,
    state: {
      pagination
    },
    getCoreRowModel: getCoreRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFacetedRowModel: getFacetedRowModel(),
    getFacetedUniqueValues: getFacetedUniqueValues(),
    getFacetedMinMaxValues: getFacetedMinMaxValues(),
    enableSortingRemoval: false,
    getPaginationRowModel: getPaginationRowModel(),
    onPaginationChange: setPagination
  })

  const { pages, showLeftEllipsis, showRightEllipsis } = usePagination({
    currentPage: table.getState().pagination.pageIndex + 1,
    totalPages: table.getPageCount(),
    paginationItemsToDisplay: 2
  })

  return (
    <div className='w-full'>
      <div className='border-b'>
        <div className='flex min-h-17 flex-wrap items-center justify-between gap-3 px-6 py-3'>
          <span className='font-medium'>On route vehicle</span>
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant='ghost' className='text-muted-foreground size-8 rounded-full'>
                <EllipsisVerticalIcon />
                <span className='sr-only'>Menu</span>
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align='end'>
              <DropdownMenuGroup>
                {listItems.map((item, index) => (
                  <DropdownMenuItem key={index}>{item}</DropdownMenuItem>
                ))}
              </DropdownMenuGroup>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
        <Table>
          <TableHeader>
            {table.getHeaderGroups().map(headerGroup => (
              <TableRow key={headerGroup.id} className='h-14 border-t'>
                {headerGroup.headers.map(header => {
                  return (
                    <TableHead
                      key={header.id}
                      style={{ width: `${header.getSize()}px` }}
                      className='text-muted-foreground first:pl-4 last:px-4'
                    >
                      {header.isPlaceholder ? null : header.column.getCanSort() ? (
                        <div
                          className={cn(
                            header.column.getCanSort() &&
                              'flex h-full cursor-pointer items-center justify-between gap-2 select-none'
                          )}
                          onClick={header.column.getToggleSortingHandler()}
                          onKeyDown={e => {
                            if (header.column.getCanSort() && (e.key === 'Enter' || e.key === ' ')) {
                              e.preventDefault()
                              header.column.getToggleSortingHandler()?.(e)
                            }
                          }}
                          tabIndex={header.column.getCanSort() ? 0 : undefined}
                        >
                          {flexRender(header.column.columnDef.header, header.getContext())}
                          {{
                            asc: <ChevronUpIcon className='shrink-0 opacity-60' size={16} aria-hidden='true' />,
                            desc: <ChevronDownIcon className='shrink-0 opacity-60' size={16} aria-hidden='true' />
                          }[header.column.getIsSorted() as string] ?? null}
                        </div>
                      ) : (
                        flexRender(header.column.columnDef.header, header.getContext())
                      )}
                    </TableHead>
                  )
                })}
              </TableRow>
            ))}
          </TableHeader>
          <TableBody>
            {table.getRowModel().rows?.length ? (
              table.getRowModel().rows.map(row => (
                <TableRow key={row.id} data-state={row.getIsSelected() && 'selected'}>
                  {row.getVisibleCells().map(cell => (
                    <TableCell key={cell.id} className='h-17 first:pl-4 last:px-4'>
                      {flexRender(cell.column.columnDef.cell, cell.getContext())}
                    </TableCell>
                  ))}
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell colSpan={columns.length} className='h-24 text-center'>
                  No results.
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>

      <div className='flex items-center justify-between gap-3 px-6 py-4 max-sm:flex-col'>
        <p className='text-muted-foreground text-sm whitespace-nowrap' aria-live='polite'>
          Showing{' '}
          <span>
            {table.getState().pagination.pageIndex * table.getState().pagination.pageSize + 1} to{' '}
            {Math.min(
              Math.max(
                table.getState().pagination.pageIndex * table.getState().pagination.pageSize +
                  table.getState().pagination.pageSize,
                0
              ),
              table.getRowCount()
            )}
          </span>{' '}
          of <span>{table.getRowCount().toString()} entries</span>
        </p>

        <div>
          <Pagination>
            <PaginationContent>
              <PaginationItem>
                <Button
                  className='disabled:pointer-events-none disabled:opacity-50'
                  variant={'ghost'}
                  onClick={() => table.previousPage()}
                  disabled={!table.getCanPreviousPage()}
                  aria-label='Go to previous page'
                >
                  <ChevronLeftIcon size={16} aria-hidden='true' />
                  Previous
                </Button>
              </PaginationItem>

              {showLeftEllipsis && (
                <PaginationItem>
                  <PaginationEllipsis />
                </PaginationItem>
              )}

              {pages.map(page => {
                const isActive = page === table.getState().pagination.pageIndex + 1

                return (
                  <PaginationItem key={page}>
                    <Button
                      size='icon'
                      className={`${!isActive && 'bg-primary/10 text-primary hover:bg-primary/20 focus-visible:ring-primary/20 dark:focus-visible:ring-primary/40'}`}
                      onClick={() => table.setPageIndex(page - 1)}
                      aria-current={isActive ? 'page' : undefined}
                    >
                      {page}
                    </Button>
                  </PaginationItem>
                )
              })}

              {showRightEllipsis && (
                <PaginationItem>
                  <PaginationEllipsis />
                </PaginationItem>
              )}

              <PaginationItem>
                <Button
                  className='disabled:pointer-events-none disabled:opacity-50'
                  variant={'ghost'}
                  onClick={() => table.nextPage()}
                  disabled={!table.getCanNextPage()}
                  aria-label='Go to next page'
                >
                  Next
                  <ChevronRightIcon size={16} aria-hidden='true' />
                </Button>
              </PaginationItem>
            </PaginationContent>
          </Pagination>
        </div>
      </div>
    </div>
  )
}

export default VehicleRouteDatatable

import { cn } from '@/lib/utils'

interface CircularProgressProps {
  value: number
  renderLabel?: (progress: number) => number | string
  size?: number
  strokeWidth?: number
  circleStrokeWidth?: number
  progressStrokeWidth?: number
  shape?: 'square' | 'round'
  className?: string
  progressClassName?: string
  labelClassName?: string
  showLabel?: boolean
}

const CircularProgress = ({
  value,
  renderLabel,
  className,
  progressClassName,
  labelClassName,
  showLabel,
  shape = 'round',
  size = 100,
  strokeWidth,
  circleStrokeWidth = 10,
  progressStrokeWidth = 10
}: CircularProgressProps) => {
  const radius = size / 2
  const circumference = Math.ceil(3.14 * radius * 2)
  const percentage = Math.ceil(circumference * ((100 - value) / 100))
  const viewBox = `-${size * 0.125} -${size * 0.125} ${size * 1.25} ${size * 1.25}`

  return (
    <div className='relative'>
      <svg
        width={size}
        height={size}
        viewBox={viewBox}
        version='1.1'
        xmlns='http://www.w3.org/2000/svg'
        style={{ transform: 'rotate(-90deg)' }}
        className='relative'
      >
        {/* Base Circle */}
        <circle
          r={radius}
          cx={size / 2}
          cy={size / 2}
          fill='transparent'
          strokeWidth={strokeWidth ?? circleStrokeWidth}
          strokeDasharray={circumference}
          strokeDashoffset='0'
          className={cn('stroke-primary/25', className)}
        />
        {/* Progress */}
        <circle
          r={radius}
          cx={size / 2}
          cy={size / 2}
          strokeWidth={strokeWidth ?? progressStrokeWidth}
          strokeLinecap={shape}
          strokeDashoffset={percentage}
          fill='transparent'
          strokeDasharray={circumference}
          className={cn('stroke-primary', progressClassName)}
        />
      </svg>
      {showLabel && (
        <div className={cn('text-md absolute inset-0 flex items-center justify-center', labelClassName)}>
          {renderLabel ? renderLabel(value) : value}%
        </div>
      )}
    </div>
  )
}

export { CircularProgress }

'use client'

import * as React from 'react'

import { cva, type VariantProps } from 'class-variance-authority'
import { StarIcon, type LucideProps } from 'lucide-react'

import { cn } from '@/lib/utils'

// Variants
const ratingVariants = cva('transition-colors', {
  variants: {
    variant: {
      default: 'text-foreground fill-current',
      destructive: 'text-destructive fill-current',
      outline: 'text-muted-foreground fill-transparent stroke-current',
      secondary: 'text-secondary-foreground fill-current',
      yellow: 'fill-current text-amber-600 dark:text-amber-400'
    }
  },
  defaultVariants: {
    variant: 'default'
  }
})

// Constants
const RATING_DEFAULTS = {
  precision: 1,
  maxStars: 5,
  size: 20,
  variant: 'default' as const,
  icon: <StarIcon />
} as const

// Types
interface RatingItemProps extends React.ComponentProps<'label'> {
  variant?: VariantProps<typeof ratingVariants>['variant']
  size: number
  value: number
  hoveredValue: number | null
  point: number
  name: string
  readOnly?: boolean
  disabled?: boolean
  precision: number
  Icon: React.ReactElement<LucideProps>
  onMouseLeave: React.MouseEventHandler<HTMLLabelElement>
  onValueHover: (value: number) => void
  onValueChange?: (value: number) => void
}

interface RatingProps extends React.ComponentProps<'div'> {
  value?: number
  defaultValue?: number
  name?: string
  max?: number
  size?: number
  icon?: React.ReactElement<LucideProps>
  variant?: VariantProps<typeof ratingVariants>['variant']
  readOnly?: boolean
  disabled?: boolean
  precision?: number
  onValueChange?: (value: number) => void
  onValueHover?: (value: number) => void
}

// Rating Item Component
function RatingItem({
  size,
  variant = 'default',
  value,
  point,
  hoveredValue,
  name,
  readOnly = false,
  disabled = false,
  precision,
  Icon,
  onMouseLeave,
  onValueChange,
  onValueHover,
  className,
  ...props
}: RatingItemProps) {
  const Comp = readOnly ? 'span' : 'label'
  const id = React.useId()
  const ratingIconId = `rating-icon-${id}`
  const isInteractive = !readOnly && !disabled
  const partialPoint = point % 1
  const isPartialPoint = partialPoint !== 0
  const shouldShowFilled = (hoveredValue || value) >= point
  const partialPointWidth = isPartialPoint && shouldShowFilled ? `${partialPoint * 100}%` : undefined

  const icons = React.useMemo(() => {
    const emptyIcon = React.cloneElement(Icon, {
      size,
      className: cn(
        'fill-muted-foreground/20 stroke-muted-foreground/10',
        variant === 'yellow' && 'fill-amber-600/30 stroke-amber-600/10 dark:fill-amber-400/30 dark:stroke-amber-400/10'
      ),
      'aria-hidden': 'true'
    })

    const fullIcon = React.cloneElement(Icon, {
      size,
      className: cn(ratingVariants({ variant })),
      'aria-hidden': 'true'
    })

    return { emptyIcon, fullIcon }
  }, [Icon, size, variant])

  const getRatingPoint = React.useCallback(
    (event: React.MouseEvent<HTMLLabelElement>) => {
      const { left, width } = event.currentTarget.getBoundingClientRect()

      if (width === 0 || precision <= 0 || precision > 1) return 0
      const x = event.clientX - left
      const fillRatio = x / width
      const base = Math.ceil(point) - 1

      return base + Math.ceil(fillRatio / precision) * precision
    },
    [precision, point]
  )

  const handleMouseMove = React.useCallback(
    (event: React.MouseEvent<HTMLLabelElement>) => {
      if (!isInteractive) return
      onValueHover(getRatingPoint(event))
    },
    [isInteractive, onValueHover, getRatingPoint]
  )

  const handleClick = React.useCallback(
    (event: React.MouseEvent<HTMLLabelElement>) => {
      if (!isInteractive) return
      const newPoint = getRatingPoint(event)

      onValueHover(0)
      onValueChange?.(newPoint === value ? 0 : newPoint)

      // Prevent focus on click by blurring the element
      event.currentTarget.blur()
    },
    [isInteractive, value, onValueChange, onValueHover, getRatingPoint]
  )

  return (
    <>
      <Comp
        data-slot='rating-item'
        htmlFor={readOnly ? undefined : `${ratingIconId}-${point}`}
        aria-label={`${point} Stars`}
        onClick={!readOnly ? handleClick : undefined}
        onMouseMove={!readOnly ? handleMouseMove : undefined}
        onMouseLeave={!readOnly ? onMouseLeave : undefined}
        data-disabled={disabled}
        data-readonly={readOnly}
        data-filled={shouldShowFilled}
        className={cn(
          '[&_svg]:pointer-events-none',
          isPartialPoint && 'pointer-events-none absolute top-0 left-0 overflow-hidden',
          isInteractive && 'cursor-pointer hover:scale-105',
          disabled && 'cursor-not-allowed opacity-50',
          className
        )}
        style={{ width: partialPointWidth }}
        {...props}
      >
        {!isPartialPoint && !shouldShowFilled && icons.emptyIcon}
        {shouldShowFilled && icons.fullIcon}
      </Comp>
      {!readOnly && (
        <input
          type='radio'
          id={`${ratingIconId}-${point}`}
          name={name}
          value={point}
          className='sr-only'
          tabIndex={-1}
          data-slot='rating-input'
        />
      )}
    </>
  )
}

// Rating Component
function Rating({
  value: controlledValue,
  defaultValue = 0,
  name,
  max = RATING_DEFAULTS.maxStars,
  size = RATING_DEFAULTS.size,
  icon: Icon = RATING_DEFAULTS.icon,
  variant = RATING_DEFAULTS.variant,
  className,
  readOnly = false,
  disabled = false,
  precision = RATING_DEFAULTS.precision,
  onValueChange,
  onValueHover,
  ...props
}: RatingProps) {
  const id = React.useId()
  const ratingName = name ?? `rating-${id}`
  const [internalValue, setInternalValue] = React.useState<number>(defaultValue)
  const [hoveredValue, setHoveredValue] = React.useState<number>(0)
  const isControlled = controlledValue !== undefined
  const value = isControlled ? controlledValue : internalValue
  const isInteractive = !readOnly && !disabled

  const handleValueChange = React.useCallback(
    (newValue: number) => {
      if (!isControlled) {
        setInternalValue(newValue)
      }

      onValueChange?.(newValue)
    },
    [isControlled, onValueChange]
  )

  const handleValueHover = React.useCallback(
    (point: number) => {
      setHoveredValue(point)
      onValueHover?.(point)
    },
    [onValueHover]
  )

  const handleKeyDown = React.useCallback(
    (event: React.KeyboardEvent<HTMLDivElement>) => {
      if (!isInteractive) return

      switch (event.key) {
        case 'ArrowRight':
        case 'ArrowUp':
          event.preventDefault()

          if (value + precision > max) {
            handleValueChange(0)
          } else {
            handleValueChange(value + precision)
          }

          break
        case 'ArrowLeft':
        case 'ArrowDown':
          event.preventDefault()

          if (value - precision < 0) {
            handleValueChange(max)
          } else {
            handleValueChange(value - precision)
          }

          break
        case ' ':
        case 'Enter':
          event.preventDefault()

          // If no rating is set, set to first step, otherwise clear rating
          if (value === 0) {
            handleValueChange(precision)
          } else {
            handleValueChange(0)
          }

          break
        case 'Home':
          event.preventDefault()
          handleValueChange(precision)

          break
        case 'End':
          event.preventDefault()
          handleValueChange(max)

          break
        default:
          break
      }
    },
    [isInteractive, value, max, precision, handleValueChange]
  )

  const handleMouseDown = React.useCallback((event: React.MouseEvent<HTMLDivElement>) => {
    // Prevent focus on mouse click
    event.preventDefault()
  }, [])

  const stars = React.useMemo(() => {
    if (precision <= 0 || precision > 1) {
      console.warn('Rating: precision must be greater than 0 and less than or equal to 1')

      return []
    }

    return Array.from({ length: max }, (_, index) => ({
      key: index,
      points: Array.from({ length: Math.floor(1 / precision) }, (_, i) => index + precision * (i + 1))
    }))
  }, [max, precision])

  return (
    <div
      data-slot='rating'
      role={!readOnly ? 'radiogroup' : 'img'}
      onKeyDown={!readOnly ? handleKeyDown : undefined}
      onMouseDown={!readOnly ? handleMouseDown : undefined}
      tabIndex={!readOnly && !disabled ? 0 : undefined}
      data-disabled={disabled}
      data-readonly={readOnly}
      className={cn(
        'focus-visible:ring-ring/50 flex gap-px focus-visible:ring-2 focus-visible:ring-offset-2 focus-visible:outline-none',
        disabled && 'opacity-50',
        className
      )}
      aria-label={readOnly ? `${value} stars` : 'Rating'}
      aria-valuemin={0}
      aria-valuemax={max}
      aria-valuenow={value}
      aria-valuetext={`${value} of ${max} stars`}
      {...props}
    >
      {stars.map(({ key, points }) => (
        <span
          key={key}
          data-slot='rating-star'
          className={cn(
            'relative',
            isInteractive && 'transition-transform hover:scale-110',
            disabled && 'cursor-not-allowed'
          )}
          aria-disabled={disabled}
          aria-hidden={readOnly}
        >
          {points.map(point => (
            <RatingItem
              key={point}
              name={ratingName}
              disabled={disabled}
              hoveredValue={hoveredValue}
              point={point}
              precision={precision}
              readOnly={readOnly}
              size={size}
              value={value}
              variant={variant}
              Icon={Icon}
              onMouseLeave={() => setHoveredValue(0)}
              onValueHover={handleValueHover}
              onValueChange={handleValueChange}
            />
          ))}
        </span>
      ))}
    </div>
  )
}

export { Rating, ratingVariants, type RatingProps }

type UsePaginationProps = {
  currentPage: number
  totalPages: number
  paginationItemsToDisplay: number
}

type UsePaginationReturn = {
  pages: number[]
  showLeftEllipsis: boolean
  showRightEllipsis: boolean
}

export function usePagination({
  currentPage,
  totalPages,
  paginationItemsToDisplay
}: UsePaginationProps): UsePaginationReturn {
  function calculatePaginationRange(): number[] {
    if (totalPages <= paginationItemsToDisplay) {
      return Array.from({ length: totalPages }, (_, i) => i + 1)
    }

    const halfDisplay = Math.floor(paginationItemsToDisplay / 2)

    const initialRange = {
      start: currentPage - halfDisplay,
      end: currentPage + halfDisplay
    }

    const adjustedRange = {
      start: Math.max(1, initialRange.start),
      end: Math.min(totalPages, initialRange.end)
    }

    if (adjustedRange.start === 1) {
      adjustedRange.end = Math.min(paginationItemsToDisplay, totalPages)
    }

    if (adjustedRange.end === totalPages) {
      adjustedRange.start = Math.max(1, totalPages - paginationItemsToDisplay + 1)
    }

    return Array.from({ length: adjustedRange.end - adjustedRange.start + 1 }, (_, i) => adjustedRange.start + i)
  }

  const pages = calculatePaginationRange()

  // Determine ellipsis display based on the actual pages shown
  const showLeftEllipsis = pages.length > 0 && pages[0] > 1 && pages[0] > 2

  const showRightEllipsis =
    pages.length > 0 && pages[pages.length - 1] < totalPages && pages[pages.length - 1] < totalPages - 1

  return {
    pages,
    showLeftEllipsis,
    showRightEllipsis
  }
}
