    href: '#'
  },
  {
    title: 'Blocks',
    href: '#'
  },
  {
    title: 'Templates',
    href: '#'
  },
  {
    title: 'Resources',
    items: [
      { title: 'UI kits', href: '#' },
      { title: 'Component library', href: '#' },
      { title: 'Figma designs', href: '#' }
