            >
              {portfolios
                .filter((_, index) => index % 4 === colIndex)
                .map((project, index) => (
                  <MotionPreset
                    key={index}
                    fade
                    delay={
                      0.9 +
                      ((Array.from({ length: colIndex }).reduce(
                        (sum: number, _, i) => sum + portfolios.filter((_, idx) => idx % 4 === i).length,
                        0
