import Portfolio from '@/components/shadcn-studio/blocks/portfolio-07/portfolio-07'
import type { PortfolioItem } from '@/components/shadcn-studio/blocks/portfolio-07/portfolio-07'

const portfolios: PortfolioItem[] = [
  {
    image: 'https://cdn.shadcnstudio.com/ss-assets/blocks/marketing/portfolio/image-22.png',
    title: 'TravelMate App',
    description:
      'An application designed for travelers that helps users find and book accommodations, activities, and local experiences seamlessly. Features include real-time availability, user reviews, and personalized recommendations.'
  },
  {
    image: 'https://cdn.shadcnstudio.com/ss-assets/blocks/marketing/portfolio/image-81.png',
    title: 'Wealth Platform',
    description:
      'A next-generation wealth management platform that empowers financial advisors to manage their next-gen clients onchain. Our solution integrates traditional and digital assets with powerful analytics tools.'
  },
  {
    image: 'https://cdn.shadcnstudio.com/ss-assets/blocks/marketing/portfolio/image-82.png',
    title: 'EcoTrack Platform',
    description:
      'An innovative sustainability monitoring solution that helps businesses track and reduce their environmental impact. Our platform provides detailed analytics for achieving corporate sustainability goals.'
  },
  {
    image: 'https://cdn.shadcnstudio.com/ss-assets/blocks/marketing/portfolio/image-83.png',
    title: 'LearnSmart LMS',
    description:
      'A modern learning management system that revolutionizes online education through personalized learning paths and interactive content delivery. Our platform enhances student engagement and educational outcomes.'
  }
]

const PortfolioPage = () => {
  return <Portfolio portfolios={portfolios} />
}

export default PortfolioPage

import { Badge } from '@/components/ui/badge'

import PortfolioContent from '@/components/shadcn-studio/blocks/portfolio-07/portfolio-content'

export type PortfolioItem = {
  image: string
  title: string
  description: string
}

type PortfolioProps = {
  portfolios: PortfolioItem[]
}

const Portfolio = ({ portfolios }: PortfolioProps) => {
  return (
    <section className='py-8 sm:py-16 lg:py-24'>
      <div className='mx-auto max-w-7xl space-y-12 px-4 sm:space-y-16 sm:px-6 lg:space-y-24 lg:px-8'>
        {/* Section Header */}
        <div className='space-y-4 text-center'>
          <Badge variant='outline' className='rounded-full text-sm font-normal'>
            Our portfolio
          </Badge>

          <h2 className='text-2xl font-semibold md:text-3xl lg:text-4xl'>Our Creative Showcase</h2>

          <p className='text-muted-foreground text-xl'>
            Dive into our extensive portfolio showcasing a diverse range of innovative projects, cutting-edge designs,
            and impactful solutions that push boundaries, drive success, and redefine industry standards.
          </p>
        </div>

        {/* Portfolio Carousel */}
        <PortfolioContent portfolios={portfolios} />
      </div>
    </section>
  )
}

export default Portfolio

'use client'

import { useEffect, useRef, useState } from 'react'

import { annotate } from 'rough-notation'

import { Button } from '@/components/ui/button'
import {
  Carousel,
  CarouselContent,
  CarouselItem,
  CarouselNext,
  CarouselPrevious,
  type CarouselApi
} from '@/components/ui/carousel'

import { cn } from '@/lib/utils'

type Portfolio = {
  image: string
  title: string
  description: string
}

const PortfolioContent = ({ portfolios }: { portfolios: Portfolio[] }) => {
  const [api, setApi] = useState<CarouselApi>()
  const [current, setCurrent] = useState(0)
  const [count, setCount] = useState(0)

  const elementRef = useRef<HTMLHeadingElement>(null)
  const annotationRef = useRef<any>(null)

  useEffect(() => {
    if (!api) {
      return
    }

    setCount(api.scrollSnapList().length)
    setCurrent(api.selectedScrollSnap())

    api.on('select', () => {
      setCurrent(api.selectedScrollSnap())
    })
  }, [api])

  useEffect(() => {
    const element = elementRef.current

    if (annotationRef.current) {
      annotationRef.current.remove()
      annotationRef.current = null
    }

    if (element) {
      const timer = setTimeout(() => {
        const annotation = annotate(element, {
          type: 'underline',
          color: 'var(--primary)',
          strokeWidth: 2,
          animationDuration: 700,
          iterations: 2,
          padding: 2,
          multiline: true
        })

        annotation.show()
        annotationRef.current = annotation
      }, 300)

      return () => {
        clearTimeout(timer)

        if (annotationRef.current) {
          annotationRef.current.remove()
          annotationRef.current = null
        }
      }
    }

    return () => {
      if (annotationRef.current) {
        annotationRef.current.remove()
        annotationRef.current = null
      }
    }
  }, [current])

  return (
    <Carousel setApi={setApi} className='space-y-12 sm:space-y-16 lg:space-y-24'>
      <CarouselContent>
        {portfolios.map((project, index) => (
          <CarouselItem key={index} className='grid items-center gap-6 md:grid-cols-2 md:gap-14'>
            <img src={project.image} alt={project.title} className='h-96 w-full rounded-lg object-cover' />
            <div className='space-y-8'>
              <h3 ref={index === current ? elementRef : null} className='relative inline-block text-2xl font-semibold'>
                {project.title}
              </h3>
              <p className='text-muted-foreground'>{project.description}</p>
              <Button size='lg' asChild>
                <a href='#'>View Details</a>
              </Button>
            </div>
          </CarouselItem>
        ))}
      </CarouselContent>

      <div className='flex items-center justify-center gap-6'>
        <CarouselPrevious variant='ghost' className='static size-9 translate-y-0' />
        <div className='flex gap-2'>
          {Array.from({ length: count }).map((_, index) => (
            <button
              key={index}
              className={cn(
                'size-3.5 cursor-pointer rounded-full transition-colors',
                index === current ? 'bg-primary' : 'bg-muted'
              )}
              onClick={() => api?.scrollTo(index)}
              aria-label={`Go to slide ${index + 1}`}
            />
          ))}
        </div>
        <CarouselNext variant='ghost' className='static size-9 translate-y-0' />
      </div>
    </Carousel>
  )
}

export default PortfolioContent
