'use client'

import { useEffect, useState } from 'react'

import { TruckIcon, StoreIcon, MapPinIcon, HeartIcon, ShoppingCartIcon, CheckIcon, ChevronUpIcon } from 'lucide-react'

import { Button } from '@/components/ui/button'
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group'
import { Carousel, CarouselContent, CarouselItem, type CarouselApi } from '@/components/ui/carousel'
import { Rating } from '@/components/ui/rating'
import { Badge } from '@/components/ui/badge'
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from '@/components/ui/collapsible'
import { Label } from '@/components/ui/label'
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger
} from '@/components/ui/dropdown-menu'

import { cn } from '@/lib/utils'

type ProductOverviewProps = {
  productItems: {
    name: string
    inStock: boolean
    address?: string
    totalReview: number
    rating: number
    price: number
    discountPercentage?: number
    hasDiscount?: boolean
    images: Array<{
      src: string
      alt: string
    }>
    deliveryOption: Array<{
      id: string
      shippingCharges: number | string
      subText: string
      estimatedDelivery: string
      shippingType: string
    }>
    paymentOption: Array<{
      id: string
      context: string
      islink?: boolean
      href?: string
      linkText?: string
    }>
  }[]
  benefits: {
    list: string
  }[]
}

const ProductOverview = ({ productItems, benefits }: ProductOverviewProps) => {
  const [selectedImage, setSelectedImage] = useState(0)
  const [api, setApi] = useState<CarouselApi>()

  const [selectedDeliveryOption, setSelectedDeliveryOption] = useState<{
    id: string
    shippingCharges: number | string
    subText: string
    estimatedDelivery: string
    shippingType: string
  } | null>(null)

  // Sync carousel with thumbnail selection
  useEffect(() => {
    if (!api) return

    api.scrollTo(selectedImage)
  }, [api, selectedImage])

  // Update selectedImage when carousel changes
  useEffect(() => {
    if (!api) return

    const onSelect = () => {
      setSelectedImage(api.selectedScrollSnap())
    }

    api.on('select', onSelect)
    onSelect()

    return () => {
      api.off('select', onSelect)
    }
  }, [api])

  return (
    <section className='py-8 sm:py-16 lg:py-24'>
      <div className='mx-auto max-w-7xl px-4 sm:px-6 lg:px-8'>
        {productItems.map(item => {
          // Set default delivery option if not already set
          if (!selectedDeliveryOption && item.deliveryOption.length > 0) {
            setSelectedDeliveryOption(item.deliveryOption[0])
          }

          // Calculate prices per item
          const originalPrice = item.price
          const discountPercentage = item.discountPercentage || 0
          const hasDiscount = item.hasDiscount && discountPercentage > 0

          const finalPrice = hasDiscount ? originalPrice - (originalPrice * discountPercentage) / 100 : originalPrice
          const oneTime = finalPrice.toFixed(2)
          const monthly = (finalPrice / 24).toFixed(2)
          const fourMonthly = (finalPrice / 4).toFixed(2)

          return (
            <div key={item.name}>
              <div className='mb-8 space-y-4'>
                <h2 className='text-3xl font-semibold'>{item.name}</h2>
                <div className='flex flex-wrap items-center gap-3'>
                  <Rating readOnly variant='yellow' size={24} value={item.rating} precision={0.5} />
                  <p className='text-muted-foreground font-medium'>({item.rating})</p>
                  <a href='#' className='font-medium underline'>
                    {item.totalReview} Reviews
                  </a>
                  {item.inStock ? (
                    <p className='text-sm font-medium text-green-500'>In Stock</p>
                  ) : (
                    <p className='text-destructive text-sm font-medium'>Out of Stock</p>
                  )}

                  <div className='flex items-center gap-1.5'>
                    <MapPinIcon className='size-5.5' />
                    {item.address}
                  </div>
                </div>
              </div>
              <div className='grid grid-cols-1 gap-6 md:grid-cols-2 lg:grid-cols-3'>
                <div className='flex flex-col gap-8'>
                  <div>
                    <Carousel
                      className='bg-background w-full overflow-hidden rounded-md border'
                      setApi={setApi}
                      opts={{
                        align: 'start',
                        loop: true
                      }}
                    >
                      <CarouselContent>
                        {item.images.map((image, index) => (
                          <CarouselItem key={`${image.alt}-${index}`}>
                            <div className='h-82 p-6'>
                              <img src={image.src} alt={image.alt} className='size-full object-contain' />
                            </div>
                          </CarouselItem>
                        ))}
                      </CarouselContent>
                    </Carousel>
                  </div>
                  <div className='flex flex-wrap gap-6'>
                    {item.images.map((image, index) => (
                      <button
                        key={index}
                        type='button'
                        onClick={() => setSelectedImage(index)}
                        className={cn(
                          'shrink-0 overflow-hidden rounded-md border p-1 transition-all duration-200',
                          selectedImage === index ? 'border-primary' : ''
                        )}
                      >
                        <img src={image.src} alt={image.alt} className='size-16 object-cover' />
                      </button>
                    ))}
                  </div>
                </div>
                <div className='space-y-6'>
                  <div className='space-y-4'>
                    {selectedDeliveryOption && (
                      <>
                        <h4 className='text-lg font-semibold'>{selectedDeliveryOption.shippingType} :</h4>
                        <div className='bg-muted space-y-1 rounded-md p-3'>
                          <p className='font-semibold'> {selectedDeliveryOption.subText}</p>
                          <p className='text-muted-foreground'>{selectedDeliveryOption.estimatedDelivery}</p>
                          <p
                            className={cn(
                              'font-medium',
                              selectedDeliveryOption.shippingCharges === 'Free of charge'
                                ? 'text-green-500'
                                : 'text-foreground'
                            )}
                          >
                            {selectedDeliveryOption.shippingCharges}
                          </p>
                        </div>
                      </>
                    )}

                    <DropdownMenu>
                      <DropdownMenuTrigger asChild>
                        <Button variant='ghost'>
                          See all delivery option <ChevronUpIcon className='[[data-state=closed]>&]:rotate-180' />
                        </Button>
                      </DropdownMenuTrigger>
                      <DropdownMenuContent className='w-80'>
                        {item.deliveryOption.map(option => (
                          <DropdownMenuItem
                            key={option.id}
                            className='flex-col items-start gap-0.5'
                            onSelect={() => setSelectedDeliveryOption(option)}
                          >
                            <span className='text-sm font-semibold'>{option.shippingType}</span>
                            <span className='text-muted-foreground text-sm'>
                              {option.subText} {option.estimatedDelivery}{' '}
                            </span>
                            <span
                              className={cn(
                                'text-sm font-medium',
                                option.shippingCharges === 'Free of charge' ? 'text-green-500' : 'text-muted-foreground'
                              )}
                            >
                              {option.shippingCharges}
                            </span>
                          </DropdownMenuItem>
                        ))}
                      </DropdownMenuContent>
                    </DropdownMenu>
                  </div>
                  <Collapsible className='flex w-full flex-col items-start gap-4'>
                    <div className='text-lg font-semibold'>Benefits:</div>

                    <ul className='text-muted-foreground flex w-full flex-col gap-4'>
                      {benefits.slice(0, 3).map(task => (
                        <li key={task.list} className='flex items-center gap-2'>
                          <CheckIcon className='size-4' />
                          {task.list}
                        </li>
                      ))}
                      <CollapsibleContent className='flex flex-col gap-4'>
                        {benefits.slice(3).map(task => (
                          <li key={task.list} className='flex items-center gap-2'>
                            <CheckIcon className='size-4' />
                            {task.list}
                          </li>
                        ))}
                      </CollapsibleContent>
                    </ul>

                    <CollapsibleTrigger asChild>
                      <Button variant='ghost' size='sm'>
                        <span className='[[data-state=open]>&]:hidden'>Show more</span>
                        <span className='[[data-state=closed]>&]:hidden'>Show less</span>
                      </Button>
                    </CollapsibleTrigger>
                  </Collapsible>
                </div>
                <div className='space-y-6 lg:border-s lg:ps-6'>
                  <div className='flex gap-7'>
                    {!hasDiscount ? (
                      <h4 className='text-muted-foreground text-3xl font-bold'>${finalPrice.toFixed(2)}</h4>
                    ) : (
                      <div className='grow space-y-1'>
                        <div className='flex items-center gap-2'>
                          <span className='text-muted-foreground font-medium line-through'>
                            ${originalPrice.toFixed(2)}
                          </span>
                          <Badge className='rounded-full border-none bg-green-600/10 text-green-600 focus-visible:ring-green-600/20 focus-visible:outline-none dark:bg-green-400/10 dark:text-green-400 dark:focus-visible:ring-green-400/40 [a&]:hover:bg-green-600/5 dark:[a&]:hover:bg-green-400/5'>
                            -{discountPercentage}%
                          </Badge>
                        </div>
                        <h4 className='text-3xl font-semibold'>${finalPrice}</h4>
                      </div>
                    )}
                    <div className='space-y-2'>
                      {item.inStock ? (
                        <div className='flex items-center gap-2 text-green-500'>
                          <StoreIcon className='size-5' />
                          In Stock
                        </div>
                      ) : (
                        <div className='text-destructive flex items-center gap-2'>
                          <StoreIcon className='size-5' />
                          Out of Stock
                        </div>
                      )}

                      <div className='text-muted-foreground flex items-center gap-2'>
                        <TruckIcon className='size-5' />
                        Free Delivery
                      </div>
                    </div>
                  </div>
                  <RadioGroup className='w-full gap-3' defaultValue='financing'>
                    {item.paymentOption.map(option => {
                      // Determine which price to display based on option id
                      let priceDisplay = ''

                      if (option.id === 'oneTime') {
                        priceDisplay = `$${oneTime}`
                      } else if (option.id === 'financing') {
                        priceDisplay = `$${monthly}/mo`
                      } else if (option.id === 'installments') {
                        priceDisplay = `$${fourMonthly}/mo`
                      }

                      return (
                        <div
                          key={option.id}
                          className='border-input has-data-[state=checked]:border-primary/50 relative flex w-full gap-2 rounded-lg border p-3 shadow-xs outline-none'
                        >
                          <RadioGroupItem
                            value={option.id}
                            id={option.id}
                            aria-describedby={`${option.id}-description`}
                            className='size-4 [&_svg]:size-2'
                          />
                          <div className='flex grow flex-col gap-2'>
                            <Label htmlFor={option.id} className='flex items-center justify-between'>
                              <span>{priceDisplay}</span>
                            </Label>
                            <p id={`${option.id}-description`} className='text-muted-foreground text-sm'>
                              {option.context}{' '}
                              {option.islink && option.href ? (
                                <a href={option.href} className='text-foreground underline'>
                                  {option.linkText}
                                </a>
                              ) : null}
                            </p>
                          </div>
                        </div>
                      )
                    })}
                  </RadioGroup>

                  <div className='flex flex-row gap-4'>
                    <Button size='lg' className='grow'>
                      <ShoppingCartIcon className='size-4' />
                      Add to cart
                    </Button>
                    <Button variant='outline' className='size-10'>
                      <HeartIcon className='size-4' />
                    </Button>
                  </div>
                </div>
              </div>
            </div>
          )
        })}
      </div>
    </section>
  )
}

export default ProductOverview

'use client'

import * as React from 'react'

import { cva, type VariantProps } from 'class-variance-authority'
import { StarIcon, type LucideProps } from 'lucide-react'

import { cn } from '@/lib/utils'

// Variants
const ratingVariants = cva('transition-colors', {
  variants: {
    variant: {
      default: 'text-foreground fill-current',
      destructive: 'text-destructive fill-current',
      outline: 'text-muted-foreground fill-transparent stroke-current',
      secondary: 'text-secondary-foreground fill-current',
      yellow: 'fill-current text-amber-600 dark:text-amber-400'
    }
  },
  defaultVariants: {
    variant: 'default'
  }
})

// Constants
const RATING_DEFAULTS = {
  precision: 1,
  maxStars: 5,
  size: 20,
  variant: 'default' as const,
  icon: <StarIcon />
} as const

// Types
interface RatingItemProps extends React.ComponentProps<'label'> {
  variant?: VariantProps<typeof ratingVariants>['variant']
  size: number
  value: number
  hoveredValue: number | null
  point: number
  name: string
  readOnly?: boolean
  disabled?: boolean
  precision: number
  Icon: React.ReactElement<LucideProps>
  onMouseLeave: React.MouseEventHandler<HTMLLabelElement>
  onValueHover: (value: number) => void
  onValueChange?: (value: number) => void
}

interface RatingProps extends React.ComponentProps<'div'> {
  value?: number
  defaultValue?: number
  name?: string
  max?: number
  size?: number
  icon?: React.ReactElement<LucideProps>
  variant?: VariantProps<typeof ratingVariants>['variant']
  readOnly?: boolean
  disabled?: boolean
  precision?: number
  onValueChange?: (value: number) => void
  onValueHover?: (value: number) => void
}

// Rating Item Component
function RatingItem({
  size,
  variant = 'default',
  value,
  point,
  hoveredValue,
  name,
  readOnly = false,
  disabled = false,
  precision,
  Icon,
  onMouseLeave,
  onValueChange,
  onValueHover,
  className,
  ...props
}: RatingItemProps) {
  const Comp = readOnly ? 'span' : 'label'
  const id = React.useId()
  const ratingIconId = `rating-icon-${id}`
  const isInteractive = !readOnly && !disabled
  const partialPoint = point % 1
  const isPartialPoint = partialPoint !== 0
  const shouldShowFilled = (hoveredValue || value) >= point
  const partialPointWidth = isPartialPoint && shouldShowFilled ? `${partialPoint * 100}%` : undefined

  const icons = React.useMemo(() => {
    const emptyIcon = React.cloneElement(Icon, {
      size,
      className: cn(
        'fill-muted-foreground/20 stroke-muted-foreground/10',
        variant === 'yellow' && 'fill-amber-600/30 stroke-amber-600/10 dark:fill-amber-400/30 dark:stroke-amber-400/10'
      ),
      'aria-hidden': 'true'
    })

    const fullIcon = React.cloneElement(Icon, {
      size,
      className: cn(ratingVariants({ variant })),
      'aria-hidden': 'true'
    })

    return { emptyIcon, fullIcon }
  }, [Icon, size, variant])

  const getRatingPoint = React.useCallback(
    (event: React.MouseEvent<HTMLLabelElement>) => {
      const { left, width } = event.currentTarget.getBoundingClientRect()

      if (width === 0 || precision <= 0 || precision > 1) return 0
      const x = event.clientX - left
      const fillRatio = x / width
      const base = Math.ceil(point) - 1

      return base + Math.ceil(fillRatio / precision) * precision
    },
    [precision, point]
  )

  const handleMouseMove = React.useCallback(
    (event: React.MouseEvent<HTMLLabelElement>) => {
      if (!isInteractive) return
      onValueHover(getRatingPoint(event))
    },
    [isInteractive, onValueHover, getRatingPoint]
  )

  const handleClick = React.useCallback(
    (event: React.MouseEvent<HTMLLabelElement>) => {
      if (!isInteractive) return
      const newPoint = getRatingPoint(event)

      onValueHover(0)
      onValueChange?.(newPoint === value ? 0 : newPoint)

      // Prevent focus on click by blurring the element
      event.currentTarget.blur()
    },
    [isInteractive, value, onValueChange, onValueHover, getRatingPoint]
  )

  return (
    <>
      <Comp
        data-slot='rating-item'
        htmlFor={readOnly ? undefined : `${ratingIconId}-${point}`}
        aria-label={`${point} Stars`}
        onClick={!readOnly ? handleClick : undefined}
        onMouseMove={!readOnly ? handleMouseMove : undefined}
        onMouseLeave={!readOnly ? onMouseLeave : undefined}
        data-disabled={disabled}
        data-readonly={readOnly}
        data-filled={shouldShowFilled}
        className={cn(
          '[&_svg]:pointer-events-none',
          isPartialPoint && 'pointer-events-none absolute top-0 left-0 overflow-hidden',
          isInteractive && 'cursor-pointer hover:scale-105',
          disabled && 'cursor-not-allowed opacity-50',
          className
        )}
        style={{ width: partialPointWidth }}
        {...props}
      >
        {!isPartialPoint && !shouldShowFilled && icons.emptyIcon}
        {shouldShowFilled && icons.fullIcon}
      </Comp>
      {!readOnly && (
        <input
          type='radio'
          id={`${ratingIconId}-${point}`}
          name={name}
          value={point}
          className='sr-only'
          tabIndex={-1}
          data-slot='rating-input'
        />
      )}
    </>
  )
}

// Rating Component
function Rating({
  value: controlledValue,
  defaultValue = 0,
  name,
  max = RATING_DEFAULTS.maxStars,
  size = RATING_DEFAULTS.size,
  icon: Icon = RATING_DEFAULTS.icon,
  variant = RATING_DEFAULTS.variant,
  className,
  readOnly = false,
  disabled = false,
  precision = RATING_DEFAULTS.precision,
  onValueChange,
  onValueHover,
  ...props
}: RatingProps) {
  const id = React.useId()
  const ratingName = name ?? `rating-${id}`
  const [internalValue, setInternalValue] = React.useState<number>(defaultValue)
  const [hoveredValue, setHoveredValue] = React.useState<number>(0)
  const isControlled = controlledValue !== undefined
  const value = isControlled ? controlledValue : internalValue
  const isInteractive = !readOnly && !disabled

  const handleValueChange = React.useCallback(
    (newValue: number) => {
      if (!isControlled) {
        setInternalValue(newValue)
      }

      onValueChange?.(newValue)
    },
    [isControlled, onValueChange]
  )

  const handleValueHover = React.useCallback(
    (point: number) => {
      setHoveredValue(point)
      onValueHover?.(point)
    },
    [onValueHover]
  )

  const handleKeyDown = React.useCallback(
    (event: React.KeyboardEvent<HTMLDivElement>) => {
      if (!isInteractive) return

      switch (event.key) {
        case 'ArrowRight':
        case 'ArrowUp':
          event.preventDefault()

          if (value + precision > max) {
            handleValueChange(0)
          } else {
            handleValueChange(value + precision)
          }

          break
        case 'ArrowLeft':
        case 'ArrowDown':
          event.preventDefault()

          if (value - precision < 0) {
            handleValueChange(max)
          } else {
            handleValueChange(value - precision)
          }

          break
        case ' ':
        case 'Enter':
          event.preventDefault()

          // If no rating is set, set to first step, otherwise clear rating
          if (value === 0) {
            handleValueChange(precision)
          } else {
            handleValueChange(0)
          }

          break
        case 'Home':
          event.preventDefault()
          handleValueChange(precision)

          break
        case 'End':
          event.preventDefault()
          handleValueChange(max)

          break
        default:
          break
      }
    },
    [isInteractive, value, max, precision, handleValueChange]
  )

  const handleMouseDown = React.useCallback((event: React.MouseEvent<HTMLDivElement>) => {
    // Prevent focus on mouse click
    event.preventDefault()
  }, [])

  const stars = React.useMemo(() => {
    if (precision <= 0 || precision > 1) {
      console.warn('Rating: precision must be greater than 0 and less than or equal to 1')

      return []
    }

    return Array.from({ length: max }, (_, index) => ({
      key: index,
      points: Array.from({ length: Math.floor(1 / precision) }, (_, i) => index + precision * (i + 1))
    }))
  }, [max, precision])

  return (
    <div
      data-slot='rating'
      role={!readOnly ? 'radiogroup' : 'img'}
      onKeyDown={!readOnly ? handleKeyDown : undefined}
      onMouseDown={!readOnly ? handleMouseDown : undefined}
      tabIndex={!readOnly && !disabled ? 0 : undefined}
      data-disabled={disabled}
      data-readonly={readOnly}
      className={cn(
        'focus-visible:ring-ring/50 flex gap-px focus-visible:ring-2 focus-visible:ring-offset-2 focus-visible:outline-none',
        disabled && 'opacity-50',
        className
      )}
      aria-label={readOnly ? `${value} stars` : 'Rating'}
      aria-valuemin={0}
      aria-valuemax={max}
      aria-valuenow={value}
      aria-valuetext={`${value} of ${max} stars`}
      {...props}
    >
      {stars.map(({ key, points }) => (
        <span
          key={key}
          data-slot='rating-star'
          className={cn(
            'relative',
            isInteractive && 'transition-transform hover:scale-110',
            disabled && 'cursor-not-allowed'
          )}
          aria-disabled={disabled}
          aria-hidden={readOnly}
        >
          {points.map(point => (
            <RatingItem
              key={point}
              name={ratingName}
              disabled={disabled}
              hoveredValue={hoveredValue}
              point={point}
              precision={precision}
              readOnly={readOnly}
              size={size}
              value={value}
              variant={variant}
              Icon={Icon}
              onMouseLeave={() => setHoveredValue(0)}
              onValueHover={handleValueHover}
              onValueChange={handleValueChange}
            />
          ))}
        </span>
      ))}
    </div>
  )
}

export { Rating, ratingVariants, type RatingProps }
