import ProductQuickView from '@/components/shadcn-studio/blocks/product-quick-view-01/product-quick-view-01'
import { Button } from '@/components/ui/button'

const productImages = [
  'https://cdn.shadcnstudio.com/ss-assets/blocks/ecommerce/product-quick-view/image-6.png',
  'https://cdn.shadcnstudio.com/ss-assets/blocks/ecommerce/product-quick-view/image-10.png',
  'https://cdn.shadcnstudio.com/ss-assets/blocks/ecommerce/product-quick-view/image-11.png',
  'https://cdn.shadcnstudio.com/ss-assets/blocks/ecommerce/product-quick-view/image-12.png'
]

const paymentMethods = [
  { name: 'PayPal', imageUrl: 'https://cdn.shadcnstudio.com/ss-assets/brand-logo/american-express.png' },
  { name: 'Visa', imageUrl: 'https://cdn.shadcnstudio.com/ss-assets/brand-logo/visa.png' },
  { name: 'Mastercard', imageUrl: 'https://cdn.shadcnstudio.com/ss-assets/brand-logo/master.png' },
  { name: 'Bank Of america', imageUrl: 'https://cdn.shadcnstudio.com/ss-assets/brand-logo/boa.png' }
]

const sizes = [
  {
    value: 's',
    label: 's'
  },
  {
    value: 'm',
    label: 'm'
  },
  {
    value: 'l',
    label: 'l'
  }
]

const ProductQuickViewPage = () => {
  return (
    <div className='flex h-dvh items-start justify-center p-8'>
      <ProductQuickView
        productImages={productImages}
        paymentMethods={paymentMethods}
        sizes={sizes}
        defaultOpen
        trigger={<Button variant='outline'>Quick View</Button>}
      />
    </div>
  )
}

export default ProductQuickViewPage

'use client'

import { type ReactNode, useEffect, useState } from 'react'

import { MinusIcon, PlusIcon } from 'lucide-react'

import {
  Button as AriaButton,
  Group as AriaGroup,
  Input as AriaInput,
  NumberField as AriaNumberField
} from 'react-aria-components'

import { Carousel, CarouselContent, CarouselItem, type CarouselApi } from '@/components/ui/carousel'
import { Dialog, DialogContent, DialogDescription, DialogTitle, DialogTrigger } from '@/components/ui/dialog'
import { Button } from '@/components/ui/button'
import { cn } from '@/lib/utils'
import { Badge } from '@/components/ui/badge'
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group'
import { Rating } from '@/components/ui/rating'

type ProductQuickViewProps = {
  trigger?: ReactNode
  defaultOpen?: boolean
  productImages: string[]
  sizes: { value: string; label: string }[]
  paymentMethods: { name: string; imageUrl: string }[]
}

const ProductQuickView = ({
  defaultOpen = false,
  trigger,
  productImages,
  paymentMethods,
  sizes
}: ProductQuickViewProps) => {
  const [open, setOpen] = useState(defaultOpen)
  const [api, setApi] = useState<CarouselApi>()
  const [current, setCurrent] = useState(0)
  const [count, setCount] = useState(0)
  const [selectedSize, setSelectedSize] = useState('m')

  useEffect(() => {
    if (!api) {
      return
    }

    const handleResize = () => {
      setCount(api.scrollSnapList().length)
    }

    window.addEventListener('resize', handleResize)
    handleResize()
    setCurrent(api.selectedScrollSnap())

    api.on('select', () => {
      setCurrent(api.selectedScrollSnap())
    })

    return () => {
      window.removeEventListener('resize', handleResize)
    }
  }, [api])

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild onClick={() => setOpen(true)}>
        {trigger}
      </DialogTrigger>
      <DialogContent className='flex max-h-[calc(100%-3rem)] justify-center overflow-hidden overflow-y-auto border-0 p-0 lg:w-[calc(100%-3rem)] lg:max-w-5xl lg:overflow-visible [&_[data-slot="dialog-close"]]:flex [&_[data-slot="dialog-close"]]:size-7 [&_[data-slot="dialog-close"]]:items-center [&_[data-slot="dialog-close"]]:justify-center [&_[data-slot="dialog-close"]]:rounded-full'>
        <div className='grid grid-cols-1 items-center lg:grid-cols-2'>
          {/* Image Carousel */}
          <div className='relative h-full w-full bg-gradient-to-bl from-[#EB7EC5] to-[#433A71] lg:h-[110%] lg:rounded-3xl'>
            <Carousel
              className='h-full w-full [&>[data-slot="carousel-content"]]:overflow-visible'
              setApi={setApi}
              opts={{
                align: 'start',
                loop: true
              }}
            >
              <CarouselContent>
                {productImages.map((image, index) => (
                  <CarouselItem
                    key={index}
                    className={cn({
                      'lg:invisible': index !== current
                    })}
                  >
                    <div className='relative h-90 w-full lg:h-164'>
                      <img
                        src={image}
                        alt={`Product view ${index + 1}`}
                        className='mx-auto h-80 object-cover lg:absolute lg:top-1/2 lg:left-0 lg:h-138 lg:-translate-y-1/2 lg:rotate-30'
                      />
                    </div>
                  </CarouselItem>
                ))}
              </CarouselContent>
              <div className='absolute inset-x-0 bottom-8 flex justify-center gap-2'>
                {Array.from({ length: count }).map((_, index) => (
                  <button
                    key={index}
                    className={cn(
                      'size-2.5 cursor-pointer rounded-full transition-colors',
                      index === current ? 'bg-background' : 'bg-background/40'
                    )}
                    onClick={() => api?.scrollTo(index)}
                    aria-label={`Go to slide ${index + 1}`}
                  />
                ))}
              </div>
            </Carousel>
          </div>

          {/* Product Details */}
          <div className='flex flex-col justify-center gap-6 p-6'>
            <Badge variant='destructive' className='rounded-full px-3 py-1'>
              Sale!
            </Badge>

            <div className='space-y-4'>
              <DialogTitle className='text-3xl'>American Tourists Cute School Bag</DialogTitle>

              <div className='flex items-center gap-2'>
                <p className='text-2xl font-semibold'>$46.00</p>
                <p className='text-muted-foreground line-through'>$112.00</p>
                <p className='text-destructive font-medium'>($66 OFF)</p>
              </div>
            </div>

            <div className='flex items-center gap-1'>
              <Rating readOnly variant='yellow' size={24} value={4.5} precision={0.5} />
              <p className='text-muted-foreground'>145 customer reviews</p>
            </div>

            <DialogDescription className='text-base'>
              The American Tourister Trolley Suitcase is designed for the modern traveler, combining style, durability,
              and functionality. Crafted from high-quality, lightweight materials, this suitcase ensures easy
              maneuverability while providing ample space for your belongings.
            </DialogDescription>

            <div className='flex items-center gap-4'>
              <h4 className='text-lg font-semibold'>Bag Size :</h4>
              <RadioGroup className='flex gap-4' defaultValue={selectedSize} onValueChange={setSelectedSize}>
                {sizes.map(sizeItem => (
                  <label
                    key={sizeItem.value}
                    className='border-input group has-data-[state=checked]:bg-primary has-focus-visible:border-ring has-focus-visible:ring-ring/50 relative flex flex-col items-center gap-3 rounded-sm border px-2 py-1.5 text-center shadow-xs transition-[color,box-shadow] outline-none has-focus-visible:ring-[3px] has-data-disabled:cursor-not-allowed has-data-disabled:opacity-50'
                  >
                    <RadioGroupItem
                      id={sizeItem.value}
                      value={sizeItem.value}
                      className='sr-only after:absolute after:inset-0'
                      aria-label={`size-radio-${sizeItem.value}`}
                    />
                    <p className='text-foreground group-has-data-[state=checked]:text-primary-foreground text-sm leading-none font-medium uppercase'>
                      {sizeItem.label}
                    </p>
                  </label>
                ))}
              </RadioGroup>
            </div>

            <div className='flex gap-4'>
              <AriaNumberField defaultValue={1} minValue={1} aria-label='Quantity'>
                <AriaGroup className='dark:bg-input/30 border-input data-focus-within:border-ring data-focus-within:ring-ring/50 data-focus-within:has-aria-invalid:ring-destructive/20 dark:data-focus-within:has-aria-invalid:ring-destructive/40 data-focus-within:has-aria-invalid:border-destructive relative inline-flex h-9 min-w-0 items-center overflow-hidden rounded-md border bg-transparent text-sm whitespace-nowrap transition-[color] outline-none data-disabled:pointer-events-none data-disabled:cursor-not-allowed data-disabled:opacity-50 data-focus-within:ring-[3px]'>
                  <AriaButton
                    slot='decrement'
                    className='bg-primary/10 hover:bg-primary/20 ms-3 flex size-5 items-center justify-center rounded transition-[color] disabled:pointer-events-none disabled:cursor-not-allowed disabled:opacity-50'
                  >
                    <MinusIcon className='size-3' />
                    <span className='sr-only'>Decrement</span>
                  </AriaButton>
                  <AriaInput className='selection:bg-primary selection:text-primary-foreground w-15 px-3 py-2 text-center tabular-nums outline-none' />
                  <AriaButton
                    slot='increment'
                    className='bg-primary/10 hover:bg-primary/20 me-3 flex size-5 items-center justify-center rounded transition-[color] disabled:pointer-events-none disabled:cursor-not-allowed disabled:opacity-50'
                  >
                    <PlusIcon className='size-3' />
                    <span className='sr-only'>Increment</span>
                  </AriaButton>
                </AriaGroup>
              </AriaNumberField>

              <Button className='grow'>Add to Cart</Button>
            </div>

            <div className='flex h-11.5 items-center justify-center gap-6 rounded-md border p-1'>
              {paymentMethods.map((method, index) => (
                <img key={index} src={method.imageUrl} alt={method.name} className='h-6' />
              ))}
            </div>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  )
}

export default ProductQuickView

'use client'

import * as React from 'react'

import { cva, type VariantProps } from 'class-variance-authority'
import { StarIcon, type LucideProps } from 'lucide-react'

import { cn } from '@/lib/utils'

// Variants
const ratingVariants = cva('transition-colors', {
  variants: {
    variant: {
      default: 'text-foreground fill-current',
      destructive: 'text-destructive fill-current',
      outline: 'text-muted-foreground fill-transparent stroke-current',
      secondary: 'text-secondary-foreground fill-current',
      yellow: 'fill-current text-amber-600 dark:text-amber-400'
    }
  },
  defaultVariants: {
    variant: 'default'
  }
})

// Constants
const RATING_DEFAULTS = {
  precision: 1,
  maxStars: 5,
  size: 20,
  variant: 'default' as const,
  icon: <StarIcon />
} as const

// Types
interface RatingItemProps extends React.ComponentProps<'label'> {
  variant?: VariantProps<typeof ratingVariants>['variant']
  size: number
  value: number
  hoveredValue: number | null
  point: number
  name: string
  readOnly?: boolean
  disabled?: boolean
  precision: number
  Icon: React.ReactElement<LucideProps>
  onMouseLeave: React.MouseEventHandler<HTMLLabelElement>
  onValueHover: (value: number) => void
  onValueChange?: (value: number) => void
}

interface RatingProps extends React.ComponentProps<'div'> {
  value?: number
  defaultValue?: number
  name?: string
  max?: number
  size?: number
  icon?: React.ReactElement<LucideProps>
  variant?: VariantProps<typeof ratingVariants>['variant']
  readOnly?: boolean
  disabled?: boolean
  precision?: number
  onValueChange?: (value: number) => void
  onValueHover?: (value: number) => void
}

// Rating Item Component
function RatingItem({
  size,
  variant = 'default',
  value,
  point,
  hoveredValue,
  name,
  readOnly = false,
  disabled = false,
  precision,
  Icon,
  onMouseLeave,
  onValueChange,
  onValueHover,
  className,
  ...props
}: RatingItemProps) {
  const Comp = readOnly ? 'span' : 'label'
  const id = React.useId()
  const ratingIconId = `rating-icon-${id}`
  const isInteractive = !readOnly && !disabled
  const partialPoint = point % 1
  const isPartialPoint = partialPoint !== 0
  const shouldShowFilled = (hoveredValue || value) >= point
  const partialPointWidth = isPartialPoint && shouldShowFilled ? `${partialPoint * 100}%` : undefined

  const icons = React.useMemo(() => {
    const emptyIcon = React.cloneElement(Icon, {
      size,
      className: cn(
        'fill-muted-foreground/20 stroke-muted-foreground/10',
        variant === 'yellow' && 'fill-amber-600/30 stroke-amber-600/10 dark:fill-amber-400/30 dark:stroke-amber-400/10'
      ),
      'aria-hidden': 'true'
    })

    const fullIcon = React.cloneElement(Icon, {
      size,
      className: cn(ratingVariants({ variant })),
      'aria-hidden': 'true'
    })

    return { emptyIcon, fullIcon }
  }, [Icon, size, variant])

  const getRatingPoint = React.useCallback(
    (event: React.MouseEvent<HTMLLabelElement>) => {
      const { left, width } = event.currentTarget.getBoundingClientRect()

      if (width === 0 || precision <= 0 || precision > 1) return 0
      const x = event.clientX - left
      const fillRatio = x / width
      const base = Math.ceil(point) - 1

      return base + Math.ceil(fillRatio / precision) * precision
    },
    [precision, point]
  )

  const handleMouseMove = React.useCallback(
    (event: React.MouseEvent<HTMLLabelElement>) => {
      if (!isInteractive) return
      onValueHover(getRatingPoint(event))
    },
    [isInteractive, onValueHover, getRatingPoint]
  )

  const handleClick = React.useCallback(
    (event: React.MouseEvent<HTMLLabelElement>) => {
      if (!isInteractive) return
      const newPoint = getRatingPoint(event)

      onValueHover(0)
      onValueChange?.(newPoint === value ? 0 : newPoint)

      // Prevent focus on click by blurring the element
      event.currentTarget.blur()
    },
    [isInteractive, value, onValueChange, onValueHover, getRatingPoint]
  )

  return (
    <>
      <Comp
        data-slot='rating-item'
        htmlFor={readOnly ? undefined : `${ratingIconId}-${point}`}
        aria-label={`${point} Stars`}
        onClick={!readOnly ? handleClick : undefined}
        onMouseMove={!readOnly ? handleMouseMove : undefined}
        onMouseLeave={!readOnly ? onMouseLeave : undefined}
        data-disabled={disabled}
        data-readonly={readOnly}
        data-filled={shouldShowFilled}
        className={cn(
          '[&_svg]:pointer-events-none',
          isPartialPoint && 'pointer-events-none absolute top-0 left-0 overflow-hidden',
          isInteractive && 'cursor-pointer hover:scale-105',
          disabled && 'cursor-not-allowed opacity-50',
          className
        )}
        style={{ width: partialPointWidth }}
        {...props}
      >
        {!isPartialPoint && !shouldShowFilled && icons.emptyIcon}
        {shouldShowFilled && icons.fullIcon}
      </Comp>
      {!readOnly && (
        <input
          type='radio'
          id={`${ratingIconId}-${point}`}
          name={name}
          value={point}
          className='sr-only'
          tabIndex={-1}
          data-slot='rating-input'
        />
      )}
    </>
  )
}

// Rating Component
function Rating({
  value: controlledValue,
  defaultValue = 0,
  name,
  max = RATING_DEFAULTS.maxStars,
  size = RATING_DEFAULTS.size,
  icon: Icon = RATING_DEFAULTS.icon,
  variant = RATING_DEFAULTS.variant,
  className,
  readOnly = false,
  disabled = false,
  precision = RATING_DEFAULTS.precision,
  onValueChange,
  onValueHover,
  ...props
}: RatingProps) {
  const id = React.useId()
  const ratingName = name ?? `rating-${id}`
  const [internalValue, setInternalValue] = React.useState<number>(defaultValue)
  const [hoveredValue, setHoveredValue] = React.useState<number>(0)
  const isControlled = controlledValue !== undefined
  const value = isControlled ? controlledValue : internalValue
  const isInteractive = !readOnly && !disabled

  const handleValueChange = React.useCallback(
    (newValue: number) => {
      if (!isControlled) {
        setInternalValue(newValue)
      }

      onValueChange?.(newValue)
    },
    [isControlled, onValueChange]
  )

  const handleValueHover = React.useCallback(
    (point: number) => {
      setHoveredValue(point)
      onValueHover?.(point)
    },
    [onValueHover]
  )

  const handleKeyDown = React.useCallback(
    (event: React.KeyboardEvent<HTMLDivElement>) => {
      if (!isInteractive) return

      switch (event.key) {
        case 'ArrowRight':
        case 'ArrowUp':
          event.preventDefault()

          if (value + precision > max) {
            handleValueChange(0)
          } else {
            handleValueChange(value + precision)
          }

          break
        case 'ArrowLeft':
        case 'ArrowDown':
          event.preventDefault()

          if (value - precision < 0) {
            handleValueChange(max)
          } else {
            handleValueChange(value - precision)
          }

          break
        case ' ':
        case 'Enter':
          event.preventDefault()

          // If no rating is set, set to first step, otherwise clear rating
          if (value === 0) {
            handleValueChange(precision)
          } else {
            handleValueChange(0)
          }

          break
        case 'Home':
          event.preventDefault()
          handleValueChange(precision)

          break
        case 'End':
          event.preventDefault()
          handleValueChange(max)

          break
        default:
          break
      }
    },
    [isInteractive, value, max, precision, handleValueChange]
  )

  const handleMouseDown = React.useCallback((event: React.MouseEvent<HTMLDivElement>) => {
    // Prevent focus on mouse click
    event.preventDefault()
  }, [])

  const stars = React.useMemo(() => {
    if (precision <= 0 || precision > 1) {
      console.warn('Rating: precision must be greater than 0 and less than or equal to 1')

      return []
    }

    return Array.from({ length: max }, (_, index) => ({
      key: index,
      points: Array.from({ length: Math.floor(1 / precision) }, (_, i) => index + precision * (i + 1))
    }))
  }, [max, precision])

  return (
    <div
      data-slot='rating'
      role={!readOnly ? 'radiogroup' : 'img'}
      onKeyDown={!readOnly ? handleKeyDown : undefined}
      onMouseDown={!readOnly ? handleMouseDown : undefined}
      tabIndex={!readOnly && !disabled ? 0 : undefined}
      data-disabled={disabled}
      data-readonly={readOnly}
      className={cn(
        'focus-visible:ring-ring/50 flex gap-px focus-visible:ring-2 focus-visible:ring-offset-2 focus-visible:outline-none',
        disabled && 'opacity-50',
        className
      )}
      aria-label={readOnly ? `${value} stars` : 'Rating'}
      aria-valuemin={0}
      aria-valuemax={max}
      aria-valuenow={value}
      aria-valuetext={`${value} of ${max} stars`}
      {...props}
    >
      {stars.map(({ key, points }) => (
        <span
          key={key}
          data-slot='rating-star'
          className={cn(
            'relative',
            isInteractive && 'transition-transform hover:scale-110',
            disabled && 'cursor-not-allowed'
          )}
          aria-disabled={disabled}
          aria-hidden={readOnly}
        >
          {points.map(point => (
            <RatingItem
              key={point}
              name={ratingName}
              disabled={disabled}
              hoveredValue={hoveredValue}
              point={point}
              precision={precision}
              readOnly={readOnly}
              size={size}
              value={value}
              variant={variant}
              Icon={Icon}
              onMouseLeave={() => setHoveredValue(0)}
              onValueHover={handleValueHover}
              onValueChange={handleValueChange}
            />
          ))}
        </span>
      ))}
    </div>
  )
}

export { Rating, ratingVariants, type RatingProps }
