'use client'

import { Bar, BarChart } from 'recharts'

import { Card, CardDescription, CardFooter, CardHeader, CardTitle } from '@/components/ui/card'
import { type ChartConfig, ChartContainer, ChartTooltip, ChartTooltipContent } from '@/components/ui/chart'

import { cn } from '@/lib/utils'

// Order chart data
const orderChartData = [
  { day: 'Monday', orders: 120 },
  { day: 'Tuesday', orders: 285 },
  { day: 'Wednesday', orders: 190 },
  { day: 'Thursday', orders: 190 },
  { day: 'Friday', orders: 315 },
  { day: 'Saturday', orders: 190 },
  { day: 'Sunday', orders: 220 }
]

const orderChartConfig = {
  orders: {
    label: 'Orders',
    color: 'var(--chart-1)'
  }
} satisfies ChartConfig

const StatisticsCardData = {
  title: 'Order',
  description: 'Last week',
  children: (
    <>
      <ChartContainer config={orderChartConfig} className='h-21 w-full px-2.75'>
        <BarChart
          accessibilityLayer
          data={orderChartData}
          barSize={12}
          margin={{
            left: 0,
            right: 0
          }}
        >
          <Bar
            dataKey='orders'
            fill='var(--color-orders)'
            background={{ fill: 'color-mix(in oklab, var(--primary) 10%, transparent)', radius: 12 }}
            radius={12}
          />
          <ChartTooltip cursor={false} content={<ChartTooltipContent hideLabel />} />
        </BarChart>
      </ChartContainer>
    </>
  ),
  value: '124K',
  changePercentage: '+12.6%'
}

const StatisticsOrderCard = ({ className }: { className?: string }) => {
  return (
    <Card className={cn('gap-4', className)}>
      <CardHeader className='gap-0'>
        <CardTitle className='text-lg font-semibold'>{StatisticsCardData.title}</CardTitle>
        <CardDescription className='text-muted-foreground text-base'>{StatisticsCardData.description}</CardDescription>
      </CardHeader>
      {StatisticsCardData.children}

      <CardFooter className='justify-between'>
        <span className='text-xl font-semibold'>{StatisticsCardData.value}</span>
        <span className='text-primary'>{StatisticsCardData.changePercentage}</span>
      </CardFooter>
    </Card>
  )
}

export default StatisticsOrderCard

'use client'

import { CartesianGrid, Line, LineChart } from 'recharts'

import { Card, CardDescription, CardFooter, CardHeader, CardTitle } from '@/components/ui/card'
import { type ChartConfig, ChartContainer, ChartTooltip, ChartTooltipContent } from '@/components/ui/chart'

import { cn } from '@/lib/utils'

// Profit chart data
const profitChartData = [
  { month: 'January', profit: 10 },
  { month: 'February', profit: 75 },
  { month: 'March', profit: 40 },
  { month: 'April', profit: 100 },
  { month: 'May', profit: 70 },
  { month: 'June', profit: 110 }
]

const profitChartConfig = {
  profit: {
    label: 'Profit'
  }
} satisfies ChartConfig

const StatisticsCardData = {
  title: 'Profit',
  description: 'Last Month',
  children: (
    <>
      <ChartContainer config={profitChartConfig} className='h-21 w-full px-4.5'>
        <LineChart
          accessibilityLayer
          data={profitChartData}
          margin={{
            left: 6,
            right: 6
          }}
        >
          <CartesianGrid horizontal={false} strokeDasharray='4' stroke='var(--border)' />
          <ChartTooltip cursor={false} content={<ChartTooltipContent hideLabel />} />
          <Line
            dataKey='profit'
            type='linear'
            dot={{
              r: 3.5,
              fill: 'var(--chart-2)'
            }}
            stroke='var(--chart-2)'
            strokeWidth={3}
            activeDot={{ r: 3, fill: 'var(--primary-foreground)' }}
          />
        </LineChart>
      </ChartContainer>
    </>
  ),
  value: '624K',
  changePercentage: '+12.6%'
}

const StatisticsProfitCard = ({ className }: { className?: string }) => {
  return (
    <Card className={cn('gap-4', className)}>
      <CardHeader className='gap-0'>
        <CardTitle className='text-lg font-semibold'>{StatisticsCardData.title}</CardTitle>
        <CardDescription className='text-muted-foreground text-base'>{StatisticsCardData.description}</CardDescription>
      </CardHeader>
      {StatisticsCardData.children}

      <CardFooter className='justify-between'>
        <span className='text-xl font-semibold'>{StatisticsCardData.value}</span>
        <span className='text-primary'>{StatisticsCardData.changePercentage}</span>
      </CardFooter>
    </Card>
  )
}

export default StatisticsProfitCard

'use client'

import { Label, PolarGrid, PolarRadiusAxis, RadialBar, RadialBarChart } from 'recharts'

import { Card, CardDescription, CardFooter, CardHeader, CardTitle } from '@/components/ui/card'
import { type ChartConfig, ChartContainer } from '@/components/ui/chart'

import { cn } from '@/lib/utils'

// User reach chart data
const userReachChartData = [{ visitors: 500, fill: 'var(--color-visitors)' }]

const userReachChartConfig = {
  visitors: {
    label: 'Visitors',
    color: 'var(--chart-5)'
  }
} satisfies ChartConfig

const StatisticsCardData = {
  title: 'User reach',
  description: 'Last week',
  children: (
    <>
      <ChartContainer config={userReachChartConfig} className='h-21 px-4.5'>
        <RadialBarChart data={userReachChartData} startAngle={90} endAngle={250} innerRadius={47} outerRadius={27}>
          <PolarGrid
            gridType='circle'
            radialLines={false}
            stroke='none'
            className='first:fill-primary/10 last:fill-card'
            polarRadius={[42, 32]}
          />
          <RadialBar dataKey='visitors' />
          <PolarRadiusAxis tick={false} tickLine={false} axisLine={false}>
            <Label
              content={({ viewBox }) => {
                if (viewBox && 'cx' in viewBox && 'cy' in viewBox) {
                  return (
                    <text x={viewBox.cx} y={20} textAnchor='middle' dominantBaseline='middle'>
                      <tspan
                        x={viewBox.cx}
                        y={(viewBox.cy || 0) - 8}
                        className='fill-foreground text-base font-semibold'
                      >
                        {userReachChartData[0].visitors.toLocaleString()}
                      </tspan>
                      <tspan x={viewBox.cx} y={(viewBox.cy || 0) + 15} className='fill-muted-foreground text-xs'>
                        Visitors
                      </tspan>
                    </text>
                  )
                }
              }}
            />
          </PolarRadiusAxis>
        </RadialBarChart>
      </ChartContainer>
    </>
  ),
  value: '32K',
  changePercentage: '+12%'
}

const StatisticsUserReachCard = ({ className }: { className?: string }) => {
  return (
    <Card className={cn('gap-4', className)}>
      <CardHeader className='gap-0'>
        <CardTitle className='text-lg font-semibold'>{StatisticsCardData.title}</CardTitle>
        <CardDescription className='text-muted-foreground text-base'>{StatisticsCardData.description}</CardDescription>
      </CardHeader>
      {StatisticsCardData.children}

      <CardFooter className='justify-between'>
        <span className='text-xl font-semibold'>{StatisticsCardData.value}</span>
        <span className='text-primary'>{StatisticsCardData.changePercentage}</span>
      </CardFooter>
    </Card>
  )
}

export default StatisticsUserReachCard
