'use client'

import { ArrowDownIcon, ArrowUpIcon } from 'lucide-react'
import { Area, AreaChart, XAxis } from 'recharts'

import { Card, CardContent } from '@/components/ui/card'
import { type ChartConfig, ChartContainer, ChartTooltip, ChartTooltipContent } from '@/components/ui/chart'

import { cn } from '@/lib/utils'

// Sales growth chart data
const salesGrowthChartData = [
  { day: 'Monday', sales: 260 },
  { day: 'Tuesday', sales: 380 },
  { day: 'Wednesday', sales: 250 },
  { day: 'Thursday', sales: 580 },
  { day: 'Friday', sales: 370 },
  { day: 'Saturday', sales: 420 },
  { day: 'Sunday', sales: 300 }
]

const salesGrowthChartConfig = {
  sales: {
    label: 'Sales'
  }
} satisfies ChartConfig

const StatisticsCardData = {
  title: 'Activity',
  amount: '82%',
  changePercentage: 38,
  children: (
    <>
      <ChartContainer config={salesGrowthChartConfig} className='h-36 w-full'>
        <AreaChart
          data={salesGrowthChartData}
          margin={{
            left: 12,
            right: 12
          }}
          className='stroke-2'
        >
          <defs>
            <linearGradient id='fillSales' x1='0' y1='0' x2='0' y2='1'>
              <stop offset='10%' stopColor='var(--primary)' stopOpacity={0.5} />
              <stop offset='90%' stopColor='var(--primary)' stopOpacity={0} />
            </linearGradient>
          </defs>
          <XAxis
            dataKey='day'
            tickLine={false}
            tickMargin={5.5}
            axisLine={false}
            tickFormatter={value => value.slice(0, 2)}
            className='text-muted-foreground text-sm uppercase'
          />
          <ChartTooltip cursor={false} content={<ChartTooltipContent hideLabel />} />
          <Area dataKey='sales' type='natural' fill='url(#fillSales)' stroke='var(--primary)' stackId='a' />
        </AreaChart>
      </ChartContainer>
    </>
  )
}

const StatisticsActivityCard = ({ className }: { className?: string }) => {
  return (
    <Card className={cn('gap-4', className)}>
      <div className='flex items-center justify-between gap-2 px-6'>
        <span className='font-medium'>{StatisticsCardData.title}</span>
        <span className='text-muted-foreground text-sm'>Weekly Report</span>
      </div>
      <CardContent className='flex justify-between gap-6 max-sm:flex-col'>
        <div className='flex flex-col gap-2 self-end'>
          <span className='text-5xl font-semibold'>{StatisticsCardData.amount}</span>
          <div className='text-primary flex items-center gap-1'>
            {StatisticsCardData.changePercentage > 0 ? (
              <ArrowUpIcon className='size-4' />
            ) : (
              <ArrowDownIcon className='size-4' />
            )}
            <span className='text-xs'>{StatisticsCardData.changePercentage}%</span>
          </div>
        </div>
        <div className='grow sm:pl-6'>{StatisticsCardData.children}</div>
      </CardContent>
    </Card>
  )
}

export default StatisticsActivityCard

'use client'

import { Label, Pie, PieChart } from 'recharts'

import { Card, CardContent } from '@/components/ui/card'
import { type ChartConfig, ChartContainer, ChartTooltip, ChartTooltipContent } from '@/components/ui/chart'

// Lead chart data
const leadChartData = [
  { month: 'january', sales: 340, fill: 'var(--color-january)' },
  { month: 'february', sales: 200, fill: 'var(--color-february)' },
  { month: 'march', sales: 200, fill: 'var(--color-march)' }
]

const leadChartConfig = {
  sales: {
    label: 'Sales'
  },
  january: {
    label: 'January',
    color: 'color-mix(in oklab, var(--primary) 60%, transparent)'
  },
  february: {
    label: 'February',
    color: 'var(--primary)'
  },
  march: {
    label: 'March',
    color: 'color-mix(in oklab, var(--primary) 20%, transparent)'
  }
} satisfies ChartConfig

const StatisticsCardData = {
  title: 'Generated leads',
  amount: '4,350',
  changePercentage: 18.2,
  children: (
    <>
      <ChartContainer config={leadChartConfig} className='h-37.5 w-full px-4.5'>
        <PieChart margin={{ top: 0, bottom: 0, left: 0, right: 0 }}>
          <ChartTooltip cursor={false} content={<ChartTooltipContent hideLabel />} />
          <Pie
            data={leadChartData}
            dataKey='sales'
            nameKey='month'
            innerRadius={55}
            strokeWidth={20}
            outerRadius={75}
            paddingAngle={3}
          >
            <Label
              content={({ viewBox }) => {
                if (viewBox && 'cx' in viewBox && 'cy' in viewBox) {
                  return (
                    <text x={viewBox.cx} y={viewBox.cy} textAnchor='middle' dominantBaseline='middle'>
                      <tspan x={viewBox.cx} y={viewBox.cy} className='fill-foreground text-xl font-medium'>
                        $23K
                      </tspan>
                    </text>
                  )
                }
              }}
            />
          </Pie>
        </PieChart>
      </ChartContainer>
    </>
  )
}

const StatisticsLeadCard = ({ className }: { className?: string }) => {
  return (
    <Card className={className}>
      <CardContent className='flex justify-between gap-6 max-sm:flex-col sm:items-center'>
        <div className='flex shrink-0 grow flex-col gap-6'>
          <div className='flex flex-col gap-1'>
            <span className='font-semibold'>{StatisticsCardData.title}</span>
            <span className='text-muted-foreground text-sm'>Weekly Report</span>
          </div>
          <div className='flex flex-col gap-2'>
            <span className='text-2xl font-semibold'>{StatisticsCardData.amount}</span>
            <span className='text-primary text-sm'>
              {StatisticsCardData.changePercentage > 0 ? '+' : '-'}
              {StatisticsCardData.changePercentage}%
            </span>
          </div>
        </div>
        <div className='h-37.5 sm:pl-6'>{StatisticsCardData.children}</div>
      </CardContent>
    </Card>
  )
}

export default StatisticsLeadCard

'use client'

import { Bar, BarChart, XAxis } from 'recharts'

import { Badge } from '@/components/ui/badge'
import { Card, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { type ChartConfig, ChartContainer, ChartTooltip, ChartTooltipContent } from '@/components/ui/chart'

import { cn } from '@/lib/utils'

// Profile traffic chart data
const profileTrafficChartData = [
  { index: '01', traffic: 150 },
  { index: '02', traffic: 250 },
  { index: '03', traffic: 190 },
  { index: '04', traffic: 290 },
  { index: '05', traffic: 220 },
  { index: '06', traffic: 350 },
  { index: '07', traffic: 250 }
]

const profileTrafficChartConfig = {
  traffic: {
    label: 'Traffic'
  }
} satisfies ChartConfig

const StatisticsCardData = {
  title: '2.84k',
  description: 'Average Profile Traffic',
  children: (
    <>
      <ChartContainer config={profileTrafficChartConfig} className='h-28.75 w-full px-2.75'>
        <BarChart
          accessibilityLayer
          data={profileTrafficChartData}
          barSize={12}
          margin={{
            left: -8,
            right: -8
          }}
        >
          <XAxis
            dataKey='index'
            tickLine={false}
            tickMargin={10}
            axisLine={false}
            tickFormatter={value => value.slice(0, 3)}
            className='text-muted-foreground text-sm'
          />
          <ChartTooltip cursor={false} content={<ChartTooltipContent hideLabel />} />
          <Bar dataKey='traffic' fill='var(--primary)' radius={12} />
        </BarChart>
      </ChartContainer>
    </>
  ),
  changePercentage: 15
}

const StatisticsProfileTrafficCard = ({ className }: { className?: string }) => {
  return (
    <Card className={cn('gap-6', className)}>
      <CardHeader className='gap-1'>
        <div className='flex items-center justify-between'>
          <CardTitle className='text-2xl font-semibold'>{StatisticsCardData.title}</CardTitle>
          <Badge className='bg-primary/10 text-primary'>
            {StatisticsCardData.changePercentage > 0 ? '+' : '-'}
            {StatisticsCardData.changePercentage}%
          </Badge>
        </div>
        <CardDescription className='text-muted-foreground'>{StatisticsCardData.description}</CardDescription>
      </CardHeader>
      {StatisticsCardData.children}
    </Card>
  )
}

export default StatisticsProfileTrafficCard

